class Solution:
    def fourSum(self, nums, target):
        """
        :type nums: List[int]
        :type target: int
        :rtype: List[List[int]]
        """

        result = []
        n = len(nums)
        if n < 4:
            return result

        nums.sort()   

        dict = {}
        for i in range(n):
            for j in range(i):
                sum = nums[i] + nums[j]
                if sum not in dict:
                    dict[sum] = [[j, i]]
                else:
                    dict[sum].append([j, i])

        for i in range(n):
            for j in range(i):
                sum = target - nums[i] - nums[j]
                if sum in dict:
                    for list in dict[sum]:
                        if list[0] > i and [nums[j],nums[i], nums[list[0]], nums[list[1]]] not in result:
                            result.append([nums[j],nums[i],nums[list[0]],nums[list[1]]])

        return result
```

