    def simplifyPath(self, path: 'str') -> 'str':
        dirs = [x for x in path.split('/') if x and x != '.']

        results = []

        for dir in dirs:
            if dir == '..':
                results.pop() if results else None
            else:
                results.append(dir)

        return '/' + '/'.join(results)
